mapper.select:
select posts_no, subject ,contents, nickname ,attachments ,readcount ,DATE_FORMAT(reporting_date, '%Y-%m-%d') as reporting_date
from ${board}
order by posts_no desc

select posts_no, subject ,contents, nickname ,attachments ,readcount ,DATE_FORMAT(reporting_date, '%Y-%m-%d') as reporting_date
from ${board}
where posts_no=#{no}

